
public class Switchcase1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char input='D';
		switch(input)
		{
		case'D':System.out.println("My intial is:"+input);
		break;
		case'Y':System.out.println("is my intial :"+input);
		break;
		case'Z':System.out.println("Z intial is:"+input);
		break;
		
		}

	}

}
